import React from "react";
import { Component } from "react";
import axios from "axios";
import { Navigate } from "react-router";

class EditarCliente extends Component {
    path = null;
    url = [];
    cedula = null;
    nombre = React.createRef();
    direccion = React.createRef();
    telefono = React.createRef();
    correo = React.createRef();

    state = {
        cliente: [],
        status: null
    }

    componentWillMount() {
        this.path = window.location.pathname;
        this.url = this.path.split("/");
        console.log(this.url);
        this.getCliente();
    }

    getCliente = (cedula) => {
        axios.get("http://localhost:8082/clientes/listar/" + cedula)
            .then(res => {
                this.setState({
                    cliente: res.data
                })
                console.log(res.data);
            });
    }

    enviarCliente = (e) => {
        e.preventDefault();
        alert(this.cedula.current.value);
        var cliente = {
            cedula: this.cedula.current.value,
            nombre: this.nombre.current.value,
            direccion: this.direccion.current.value,
            telefono: this.telefono.current.value,
            correo: this.correo.current.value
        }

        axios.post("http://localhost:8082/clientes/actualizar/" + this.cliente.cedula)
            .then(res => {
                this.setState({
                    status: "success"
                })
            })
    }

    render() {
        if (this.state.status === "success") {
            return <Navigate to="/clientes" />
        }
        return (
            <div class="row">
                <div class="col-md-4 mx-auto">
                    <div class="card">
                        <div class="card-header mx-auto d-block">
                            <h1>Editar cliente</h1>
                        </div>
                        <div class="card-body"></div>
                        <form onSubmit={this.enviarCliente}>
                            <div class="form-group p-2">
                                <input type="text" class="form-control" name="cedula" placeholder="Cédula" ref={this.cedula} value={this.cliente.cedula} />
                            </div>
                            <div class="form-group p-2">
                                <input type="text" class="form-control" name="nombre" placeholder="Nombre completo" ref={this.nombre} defaultValue={this.cliente.nombre} />
                            </div>
                            <div class="form-group p-2">
                                <input type="text" class="form-control" name="direccion" placeholder="Dirección" ref={this.direccion} defaultValue={this.cliente.direccion} />
                            </div>
                            <div class="form-group p-2">
                                <input type="text" class="form-control" name="telefono" placeholder="Teléfono" ref={this.telefono} defaultValue={this.cliente.telefono} />
                            </div>
                            <div class="form-group p-2">
                                <input type="text" class="form-control" name="correo" placeholder="Correo electrónico" ref={this.correo} defaultValue={this.cliente.correo} />
                            </div>
                            <br></br>
                            <div class="form-goup p-2 center">
                                <button type="submit" class="btn btn-primary btn-block">
                                    Cargar
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div >
        );
    }
}

export default EditarCliente;
