class Ventas extends Component {
    render() {
        return (
            <p>Pestaña Ventas</p>
        );
    }
}

export default Ventas; */

import React, { Component } from "react";
import axios from "axios";

class Ventas extends Component {

    cedula = React.createRef();
    nombre = React.createRef();
    codigo = React.createRef();
    producto = React.createRef();
    cantidad = React.createRef();
    total = React.createRef();
    codigo2 = React.createRef();
    producto2 = React.createRef();
    cantidad2 = React.createRef();
    total2 = React.createRef();
    codigo3 = React.createRef();
    producto3 = React.createRef();
    cantidad3 = React.createRef();
    total3 = React.createRef();
    totalVenta = React.createRef();
    iva = React.createRef();
    totalIva = React.createRef();




    state = {
        ventas: [],
        productos: [],
        status: null
    }

    setMath = function () {
        let totalVenta = this.total + this.total2 + this.total3;
        let totalIva = totalVenta * this.iva;

    }

    setData = () => {
        this.producto = this.state.productos.producto
        this.total = this.state.productos.total
    };

    getProducto = (codigo) => {
        axios.get("http://localhost:8081/productos" + codigo)
            .then(res => {
                this.setState({
                    productos: res.data
                })
            });
    };

    getCliente = (cedula) => {
        axios.get("http://localhost:8081/clientes/listar" + cedula)
            .then(res => {
                this.setState({
                    ventas: res.data
                })

            });

    };



    /*
        guardarVentas = () => {
            let tempVentas = {
                cedula: this.cedulaCliente.current.value,
                cantidadProducto: this.cantidadProducto.current.value,
                codigoProducto: this.direccion.current.value,
                valorTotal: this.telefono.current.value,
                valorVenta: this.correo.current.value
            };
    
            axios.post("http://localhost:8081/api/productos" + tempVentas) //Aqui va la url de listar
                .then(res => {
                    this.setState({
                        status: "succes"
                    })
                }); //Accion de que va a hacer el microservicio con esa info
        }
    */
    render() {



        return (
            <div>
                <h1>Productos</h1>
                <br></br>
                <table class="col-md-4 mx-auto">
                    <thead>
                    </thead>
                    <tbody>
                        <tr>
                            <td>
                                <h3>Cedula: </h3>
                            </td>
                            <td>
                                <input id="cedula" placeholder="317382091828" ref={this.cedula} />
                            </td>
                            <td>
                                <button onClick={() => { this.getCliente(this.cedula) }}>Consultar</button>
                            </td>
                            <td>
                                <h3> Cliente: </h3>
                            </td>
                            <td>
                                <input placeholder="Pepa Pink" ref={this.nombre} />
                            </td>

                        </tr>
                        <tr>
                            <td>
                                <h3> Codigo del Producto </h3>
                            </td>
                            <td>
                                <h3>  </h3>
                            </td>
                            <td>
                                <h3>Nombre Producto</h3>
                            </td>
                            <td>
                                <h3>Cantidad</h3>
                            </td>
                            <td>
                                <h3>Valor Total</h3>
                            </td>
                        </tr>
