import React, { Component } from "react";
import axios from "axios";


class Productos extends Component {

    postData(list) {
        const response = fetch('http://localhost:8083/productos/guardarArchivo', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(list)
        });
        console.log('RESPONSE: ' + response.body);
    }

    cargarArchivo() {
        var list = [];
        var csv = fileupload.files[0];
        var reader = new FileReader();
        reader.onload = function (event) {
            var text = event.target.result;
            const rows = text.slice(0).split("\n");
            for (var i = 0; i < rows.length; i++) {
                var fields = rows[i].split(",");
                const prod = new Object();
                prod.productCode = String(fields[0].trim());
                prod.productName = String(fields[1].trim());
                prod.supplierNit = String(fields[2].trim())
                prod.buyPrice = String(fields[3].trim());
                prod.iva = String(fields[4].trim());
                prod.sellPrice = String(fields[5].trim());
                list.push(prod);
            }
            console.log('LIST: ' + JSON.stringify(list));
            postData(list);
        }
        reader.readAsText(csv);
    }

    render() {
        return (
            <div class="row">
                <div class="col-md-4 mx-auto">
                    <div class="card mt-4 tex-center">
                        <div class="card-header mx-auto d-block">
                            <h1>Subir Archivo</h1>
                        </div>
                        <div class="card-body">
                            <div class="form-goup p-2 center">
                                <input type="file" accept=".csv" class="form-control" id="inputGroupFile02"/>
                            </div>
                            <div class="form-goup p-2 center">
                                <button type="submit" class="btn btn-primary btn-block">
                                    Cargar
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        );
    }
}


export default Productos;
