import React from "react";
import { Component } from "react";
import axios from "axios";
import { Navigate } from "react-router";

class EditarCliente extends Component {
    path = null;
    url = [];
    cedula = null;
    nombre = React.createRef();
    direccion = React.createRef();
    telefono = React.createRef();
    correo = React.createRef();

    state = {
        cliente: [],
        status: null
    }

    componentWillMount() {
        this.path = window.location.pathname;
        this.url = this.path.split("/");
        console.log(this.url);
        this.getCliente();
    }

    getCliente = (cedula) => {
        axios.get("http://localhost:8082/clientes/listar/" + cedula)
            .then(res => {
                this.setState({
                    cliente: res.data
                })
                console.log(res.data);
            });
    }

    enviarCliente = (e) => {
        e.preventDefault();
        alert(this.cedula.current.value);
        var cliente = {
            cedula: this.cedula.current.value,
            nombre: this.nombre.current.value,
            direccion: this.direccion.current.value,
            telefono: this.telefono.current.value,
            correo: this.correo.current.value
        }

        axios.post("http://localhost:8082/clientes/actualizar/" + this.cliente.cedula, cliente)
            .then(res => {
                this.setState({
                    status: "success"
                })
            })
    }

    render() {
        if (this.state.status === "success") {
            return <Navigate to="/clientes" />
        }
        return (
            <div>
                <h1>Editar cliente</h1>
                <form onSubmit={this.enviarCliente}>
                    <div>
                        <label >Cédula</label>
                        <input type="text" name="cedula" ref={this.cedula} defaultValue={this.state.cliente.cedula} />
                    </div>
                    <div>
                        <label>Nombre completo</label>
                        <input type="text" name="nombre" ref={this.nombre} defaultValue={this.state.cliente.nombre} />
                    </div>
                    <div>
                        <label>Dirección</label>
                        <input type="text" name="direccion" ref={this.direccion} />
                    </div>
                    <div>
                        <label>Teléfono</label>
                        <input type="text" name="telefono" ref={this.telefono} />
                    </div>
                    <div>
                        <label>Correo electrónico</label>
                        <input type="text" name="correo" ref={this.correo} />
                    </div>
                    <div>
                        <input type="submit" />
                    </div>

                </form>
            </div>

        );
    }
}

export default EditarCliente;
