import React, { Component } from "react";
import axios from "axios";


class Productos extends Component {

    //Aqui van los elementos, al guardar se actualice la pagina
    state = {
        productos: [],
        status: null
    }

    componentWillUnmount() {//Esto es para llamar cuando se arma el componente
        this.getProductos();
    }

    getProductos = () => {
        //Aqui trae todos los productos de la API
        axios.get("http://localhost:8083/productos/listar") //Aqui va la url de listar
            .then(res => {
                console.log(res.data)
                this.setState({
                    productos: res.data
                })
            }); //Accion de que va a hacer el microservicio con esa info
    }

    render() {
        return (
            <div>
                <h1>Productos</h1>
                <table>
                    <thead>
                        <tr>
                            <th> Codigo del Producto</th>
                            <th> Nombre del Producto</th>
                            
                        </tr>
                    </thead>
                </table>
            </div>

        );
    }
}


export default Productos;
