import { Navigate } from "react-router";
import React, {Component} from "react";
import axios from "axios";

class SubirProducto extends Component{

    archivo = React.createRef();

    state = {
        producto_ArchivoLista:[],
        status:null
    }

    guardarProducto = (e, state) => {
        e.preventDefault(); //Para no enviar datos por la URL para mas securite
        if(typeof e !== 'string'){
            throw TypeError('El archivo no es una cadena de texto')
        }

        let lista = e.slice(false ? e.indexOf('/n') + 1 : 0)//? ES PARA DECIRLE QUE SI ES TRUE?
        .split('/n')//No puede ir ; arriba porque crea un error ya que e.slice se cierra
        .map(l => l.split(','));

        state.producto_ArchivoLista.push(lista);

        axios.post("http://localhost:8081/api/productos" + state.producto_ArchivoLista) //Aqui va la url de listar
            .then(res => {
                this.setState({
                    status:"succes"
                })
            })
    
    }

    render(){

        if(this.state.status === "succes"){
            return <Navigate to = "/main" />
        }

        return(
            <div>
                <form onSubmit={this.guardarProducto(this.archivo, this.state)}>
                    <div class="card-body">
                        <div class="form-goup p-2 center">
                            <input type="file" accept=".csv" class="form-control" id="inputGroupFile02" name = "archivo" ref = {this.archivo}/> 
                        </div>
                        <div class="form-goup p-2 center">
                            <button onClick = {
                                () => {
                                    this.guardarProducto(this.archivo, this.state); // hay que mirar esto porque en el video lo hace con un form que llama a la funcion pero sin ningun parametro
                                }
                            } class="btn btn-primary btn-block">
                                Cargar
                            </button>
                        </div>
                    </div>
                </form>
            </div>
        );
            
    }
}



export default SubirProducto;

