import React, { Component } from "react";
import axios from "axios";
import Navigate from "react-router-dom";

class Ventas extends Component {

    cedulaCliente = React.createRef();
    cantidadProducto = React.createRef();
    codigoProducto = React.createRef();
    valorTotal = React.createRef();
    valorVenta = React.createRef();
    valorIva = React.createRef();



    state = {
        ventas: [],
        status: null
    }

    componentWillUnmount() {//Esto es para llamar cuando se arma el componente
        this.getProductos();
    }

    guardarVentas = () => {
        let tempVentas = {
            cedula: this.cedulaCliente.current.value,
            cantidadProducto: this.cantidadProducto.current.value,
            codigoProducto: this.direccion.current.value,
            valorTotal: this.telefono.current.value,
            valorVenta: this.correo.current.value
        };

        axios.post("http://localhost:8081/api/productos" + tempVentas) //Aqui va la url de listar
            .then(res => {
                this.setState({
                    status: "succes"
                })
            }); //Accion de que va a hacer el microservicio con esa info
    }

    render() {

        if (this.state.status === "succes") {
            return <Navigate to="/main" />
        }

        return (

            <div>
                <h1>Productos</h1>
                <table>
                    <thead>
                        <tr>
                            <th>Codigo</th>
                            <th>Nombre</th>
                            <th>Nit</th>
                            <th>Precio</th>
                            <th>Valor Mas IVA</th>
                            <th><input ref={this.Codigo} placeholder="Coloque Codigo del producto para Buscar" /></th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.ventas.map((reporte) => { //this.state para ver el dato. productos el nombre del array y map para recorrerlo, luego con cada iteracion (map es un bucle) se guarda en la funcion productos
                                return (//Si pongo la etiqueta div se me va a repetir un CHINGO!!!, por eso react. y esa madre porque eso es un fragmento que cumple con la funcion del DIV pero solo se creara una sola vez envolviendo todo el rellenito UwU 
                                    <React.Freagment>

                                        <tr>
                                            <td>{
                                                <input ref={this.cedulaCliente} />
                                            }</td>
                                            <td>{
                                                <input ref={this.cantidadProducto} />
                                            }</td>
                                            <td>{
                                                <input ref={this.codigoProducto} />
                                            }</td>
                                            <td>{
                                                <input ref={this.valorTotal} />
                                            }</td>
                                            <td>{
                                                <input ref={this.valorVenta} />
                                            }</td>
                                            <td>{
                                                <input ref={this.valorIva} />
                                            }</td>
                                            <td>
                                                <button onclick={this.guardarVentas()}></button>
                                            </td>
                                        </tr>

                                    </React.Freagment>
                                );
                            })
                        }
                    </tbody>
                </table>
            </div>
        );
    }
}

export default Ventas;
